import React from "react";
import { Route } from 'react-router-dom';
import ColllectionsOverview from "../../components/collections-overview/collections-overview.component";
import CollectionPage from '../collection/collection.component';
import { firestore, convertCollectionsSnapshotToMap } from '../../firebase/firebase.utils';
import {connect} from 'react-redux';
import { updateCollections } from "../../redux/shop/shop.actions";
import WithSpinner from '../../components/with-spinner/with-spinner.component';

const CollectionsOverviewWithSpinner=WithSpinner(ColllectionsOverview);
const CollectionPageWithSpinner=WithSpinner(CollectionPage);

class ShopPage extends React.Component {
state={
  loading: true
}

  unsubscribeFormSnapshot = null;

  componentDidMount() {
    const {updateCollections}=this.props;
    const collectionRef = firestore.collection('collections');
    this.unsubscribeFormSnapshot = collectionRef.onSnapshot(async snapshot => {
      const collectionsMap = convertCollectionsSnapshotToMap(snapshot)
    updateCollections(collectionsMap);
    this.setState({loading: false})
    })
  }
  render() {
    const {loading}=this.state;
    const { match } = this.props;
    return (
      <div className="shop-page">
        <Route exact path={`${match.path}`} render={
          (props)=><CollectionsOverviewWithSpinner isLoading={loading} {...props}/>} />
        <Route path={`${match.path}/:collectionId`} component={CollectionPage} />
      </div>

    )
  }
}

const mapDispatchToProps=dispatch=>({
updateCollections: collectionsMap=>dispatch(updateCollections)
})


export default connect(null,mapDispatchToProps)(ShopPage);
